import React, { Component } from "react";
import Image from 'react-bootstrap/Image'
import Container from 'react-bootstrap/Container'
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'
import Button from 'react-bootstrap/Button'


import profile from './profile.jpg';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLinkedin } from '@fortawesome/free-brands-svg-icons'
import { faFacebook } from '@fortawesome/free-brands-svg-icons'
import { faInstagram } from '@fortawesome/free-brands-svg-icons'
import { faEnvelope } from '@fortawesome/free-solid-svg-icons'




class About extends Component {

  download = () => {

    fetch('http://localhost:3000/resume.pdf')
    .then(response => {
        console.log(response)

        response.blob().then(blob => {
            let url = window.URL.createObjectURL(blob);
            let a = document.createElement('a');
            a.href = url;
            a.download = "src/resume.pdf";
            a.click();
        });
  });
}



  render() {
    return (
      <Container className="about">
          <Row>
                <Col xs = {12} md = {12} lg = {6}>
                <Image src={profile} className="profile" alt='profile' rounded/>
                </Col>
                <Col xs = {12} md = {12} lg = {6} className = "bio">
                <h3>My Story</h3>
                <p style={{marginTop: "2rem"}}>I am a junior at Brown University concentrating in Computer Science-Economics passionate about Software, Design and Product Management! Even though most of my past experiences are in software engineering and data, I have always been interested in design and decided to venture into it this semester! Outside of work catch me on the soccer field or watching a soccer game on a weekend morning!</p>
                <a href = "https://www.linkedin.com/in/rudra-srivastava1998" className = 'icon'><FontAwesomeIcon icon={faLinkedin} size="2x"/></a>
                <a href = "https://www.instagram.com/rsrivastava98" className = 'icon'><FontAwesomeIcon icon={faInstagram} size="2x"/></a>
                <a href = "https://www.facebook.com/rsrivastava98" className = 'icon'><FontAwesomeIcon icon={faFacebook} size="2x"/></a>
                <a href = "mailto:rudrasrivastava98@gmail.com" className = 'icon'><FontAwesomeIcon icon={faEnvelope} size="2x"/></a>
                <br></br>
                <Button onClick = {() => this.download()} >Resume</Button>

                </Col>
          </Row>


      </Container>
    );
  }

}

export default About;
