import React, { Component } from "react";
import main from "./main.js";
import './main.css'
import * as d3 from "d3";
import football from './football.csv'
import locations from './latlong2.csv'
import { Container } from "react-bootstrap";
import Emoji from './Emoji'


class Data_Viz extends Component {

    constructor(props){
        super(props)
    }
    componentDidMount(){
        main(d3, football, locations)

   }
  render() {
    return (
        <Container fluid>
            <div className="row" style = {{marginTop : '2rem'}}>
            <div className="hidden-div" style = {{marginTop : '2rem'}}>
            <p>Visualizations only visible on wide screen <Emoji label = "sad" emoji = "ðŸ˜”"/> </p>
            </div>
            <div className="col-lg-6">
                <div id = "filter"> 
                    <label>Set Year: </label><select id="selectButton" className = "select"></select>
                </div>


                <div id="graph1"></div>
                <div id="graph2"></div>
            </div>
            <div className="col-lg-6">

                <div id = "filter-1">
                <label>Set Top Teams: </label><select id="selectButton2" className = "select"></select>
                <label>Set Min. Number of Games: </label><select id="selectButton3" className = "select"></select>
                </div>

                <div id="graph3"></div>
            </div>

        <Container fluid style = {{marginTop: "3rem", textAlign: "center"}}>
            <p>Sources: Data from Kaggle Football Dataset <a href = "https://www.kaggle.com/martj42/international-football-results-from-1872-to-2017">here</a></p>
        </Container>
        </div>

        </Container>



    );
  }
}

export default Data_Viz;
